import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Col, ListGroup, ListGroupItem } from 'reactstrap';
import Input from "reactstrap/es/Input";
import { InputChange } from "./Utility";
import DatePicker from "react-datepicker";
import classNames from "classnames";
import "react-datepicker/dist/react-datepicker.css";
import './App.css';

class App extends React.Component {

  state = {
    modal: false,
    Description: '',
    title: '',
    temp: '',
    add: '',
    listStory: [],
    listTodo: [],
    listInProgress: [],
    listTest: [],
    listDone: [],
    startDate: new Date(),
    bgColor: ''
  };

  handleChange = date => {
    this.setState({
      startDate: date
    });
  };

  toggle = () => this.setState(prevState => ({ modal: !prevState.modal }));

  delStory = index => {
    this.setState(prevState => {
      let { listStory } = prevState;
      listStory.splice(index, 1);
      return {
        listStory
      };
    });
  };

  addToStroy = () => {
    this.setState(prevState => {
      let { listStory, title } = prevState;
      listStory.push(title);
      return { listStory, title: '', modal: false };
    });
  };

  delTodo = index => {
    this.setState(prevState => {
      let { listTodo } = prevState;
      listTodo.splice(index, 1);
      return {
        listTodo
      };
    });
  };

  addToListTodo = item => {
    this.setState(prevState => {
      let { listTodo } = prevState;
      listTodo.push(item);
      return { listTodo };
    });
  };

  delInProgress = index => {
    this.setState(prevState => {
      let { listInProgress } = prevState;
      listInProgress.splice(index, 1);
      return {
        listInProgress
      };
    });
  };

  addToListInprogress = item => {
    this.setState(prevState => {
      let { listInProgress } = prevState;
