import React from 'react';
// import myImages from '../public/assets/img';
import { Container, Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Input from "reactstrap/es/Input";
import { InputChange } from "./Utility";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import './App.css';

class App extends React.Component {

  state = {
    modal: false,
    Description: '',
    title: '',
    list: [],
    startDate: new Date()
  };

  handleChange = date => {
    this.setState({
      startDate: date
    });
  };

  toggle = () => this.setState(prevState => ({ modal: !prevState.modal }));

  // addToList = () => {
  //   const { title, Description } = this.state;
  //   this.setState(prevState => {
  //     let { list } = prevState;
  //     list.push({ title, Description });
  //     return { list, modal: false, startDate: new date, Description: '', title: '' }
  //   })
  // };

  save = () => {

  };

  render() {
    const { modal, startDate, title, Description } = this.state;
    return (
      <div className="container-fluid">

        <div className='row'>
          <header className='col-md-12'>
            <div className='col-md-3'>
              <button className='col-md-6' onClick={this.toggle}>
                <img src={process.env.PUBLIC_URL + '/assets/img/plus.png'} />
                <div className='inner-text-of-button-header'>Task</div>
              </button>

              <Modal isOpen={modal} toggle={this.toggle}>
                <ModalHeader toggle={this.toggle}>
                  <p className='modalheader-font-fix'>New Task</p>
                </ModalHeader>
                <ModalBody>
                  <Input placeholder='title' name='title' value={title} onChange={InputChange.bind(this)} />
                  <Input placeholder='Description' name='Description' value={Description} onChange={InputChange.bind(this)} />
                  <DatePicker
                    selected={this.state.startDate}
                    onChange={this.handleChange}
                    monthsShown={1}
                    inline
                    title='Deadline'
                    placeholderText='Deadline'
                  />
                </ModalBody>
                <ModalFooter>
                  <div className='fix-box-color'>
                    <Button color='danger' className='fix-box-item' />
                    <Button color='info' className='fix-box-item' />
                    <Button color='warning' className='fix-box-item' />
                    <Button color='success' className='fix-box-item' />
                  </div>
                  <Button color="secondary" onClick={this.toggle}>Cancel</Button>
                  <Button color="primary" onClick={this.addToList}>Save</Button>
                </ModalFooter>
              </Modal>


            </div>

            <div className='col-md-4 offset-md-1'>
              <button className='col-md-6'>
                <img src={process.env.PUBLIC_URL + '/assets/img/group.png'} />
                <div className='inner-text-of-button-header'>User</div>
              </button>
            </div>

            <div className='col-md-3 offset-md-1'>
              <input className='col-md-6' placeholder='Search your task' />
            </div>
          </header>
        </div>

        <div className='row'>
          <div className='col-md-2 border-right border-bottom fix'>
            Story
          </div>
          <div className='col-md-2 border-right border-bottom fix'>
            ToDo
          </div>
          <div className='col-md-4 border-right border-bottom fix'>
            In Progress
          </div>
          <div className='col-md-2 border-right border-bottom fix'>
            Test
          </div>
          <div className='col-md-2 border-bottom fix'>
            Done
          </div>
        </div>

        <div className='row full-height'>
          <div className='col-md-2 border-right'>

          </div>
          <div className='col-md-2 border-right'>

          </div>
          <div className='col-md-4 border-right'>

          </div>
          <div className='col-md-2 border-right'>

          </div>
          <div className='col-md-2'>

          </div>
        </div>
      </div>
    );
  }
}

export default App;
