import React from 'react';
import { Container, Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Input from "reactstrap/es/Input";
import { InputChange } from "./Utility";
import './App.css';

class App extends React.Component {

  state = {
    modal: false,
    phone: '',
    order: '',
    list: []
  };

  toggle = () => this.setState(prevState => ({ modal: !prevState.modal }));

  addToList = () => {
    const { phone, order } = this.state;
    this.setState(prevState => {
      let { list } = prevState;
      list.push({ phone, order });
      return { list, modal: false, phone: '', order: '' }
    })
  };

  save = () => {
    let list = [...this.state.list];
    list.sort((a, b) => {
      return a.order - b.order
    });
    list = list.map(item => item.phone);
    console.log(list);
  };

  render() {
    const { modal, phone, order } = this.state;
    return (
      <Container>
        <h1>Home</h1>
        <Button onClick={this.toggle}>Add</Button>
        <Button onClick={this.save}>Save</Button>

        <Modal isOpen={modal} toggle={this.toggle}>
          <ModalHeader className='modalheader-font-fix' toggle={this.toggle}>
            <h5>New Task</h5>
          </ModalHeader>
          <ModalBody>

            <Input placeholder='Phone' name='phone' value={phone} onChange={InputChange.bind(this)} />
            <Input placeholder='Order' name='order' value={order} onChange={InputChange.bind(this)} />

          </ModalBody>
          <ModalFooter>
            <div className='fix-box-color'>
              <Button color='danger' className='fix-box-item' />
              <Button color='info' className='fix-box-item' />
              <Button color='warning' className='fix-box-item' />
              <Button color='success' className='fix-box-item' />
            </div>
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
            <Button color="primary" onClick={this.addToList}>Save</Button>
          </ModalFooter>
        </Modal>

      </Container>
    )
  }
}

export default App;
